sys_script_include.script:
var personaManagerUtils = Class.create();
personaManagerUtils.prototype = {
    initialize: function() {
    },

	getRolesFilter: function() {
		var res = [];
		var gr = new GlideRecord("sys_properties");
		gr.get("name", "x_975207_persona_m.persona_excluded_roles");
		var roles = gr.value.split(";");

		if(roles.length > 0) { 
			roles.forEach(function(role) {
				res.push("name!=" + role.trim() + "^");
			});
		}

		return res.join("");
	},

	getGroupsFilter: function() {
		var res = [];
		var gr = new GlideRecord("sys_properties");
		gr.get("name", "x_975207_persona_m.persona_excluded_groups");
		var groups = gr.value.split(";");

		if(groups.length > 0) {
			groups.forEach(function(group) {
				res.push("name!=" + group.trim() + "^");
			});
		}

		return res.join("");
	},

    type: 'personaManagerUtils'
};
