sys_script_include.script:
var personaManagerClientHandler = Class.create();
personaManagerClientHandler.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	postSysProperties: function() {
		var data = this.getParameter("sysparm_data");
		var res = [];
		try {
			data = JSON.parse(data);

			for(key in data) {
				var gr = new GlideRecord("sys_properties");
				gr.get("name", key);

				gr.value = data[key];
				gr.update();
			}

			return JSON.stringify({ "status": 200, "message": "success" });
		} catch(e) { 
			return JSON.stringify({ "status": 400, "message": e.message });
		}
	},

    type: 'personaManagerClientHandler'
});
