sys_script_include.script:
var personaManagerClientHandler = Class.create();
personaManagerClientHandler.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	postSysProperties: function() {
		var data = this.getParameter("sysparm_data");
		var res = [];
		try {
			data = JSON.parse(data);

			for(key in data) {
				var gr = new GlideRecord("sys_properties");
				gr.get("name", key);

				gr.value = data[key];
				gr.update();
			}

			return JSON.stringify({ "status": 200, "message": "success" });
		} catch(e) { 
			return JSON.stringify({ "status": 400, "message": e.message });
		}
	},

	endSessionsFromList: function() {
		var sessions = this.getParameter("sysparm_sessionsList");

		try {
			var res = [];
			var today = new GlideDateTime();
			var gr = new GlideRecord("x_975207_persona_m_persona_sessions");
			gr.addEncodedQuery("sys_idIN" + sessions);
			gr.query();

			while(gr.next()) {
				res.push(gr.number.toString());
				gr.active = false;
				gr.end_date = today;
				gr.notes += "Session ended via UI Action.";
				gr.update();
			}

			var scriptGr = new GlideRecord("sysauto_script");
			scriptGr.addEncodedQuery("nameINDaily: Maintain User Sessions (Existing User) - End,Daily: Maintain User Sessions (Persona User) - End");
			scriptGr.query();

			while(scriptGr.next()) {
				gs.executeNow(scriptGr);
			}

			return JSON.stringify({ "status": 200, "message": "Records: " + res.join(",") + " updated." });
		} catch(e) {
			return JSON.stringify({ "status": 400, "message": e.message });
		}
	},

    type: 'personaManagerClientHandler'
});
