sysauto_script.script:
gs.info("[Persona Manager][Daily: Maintain User Sessions - End] Initialize.", "Persona Manager");

try {
	var users = [];
	var personaGr = new GlideRecord("x_975207_persona_m_persona_sessions");
	personaGr.addEncodedQuery("end_date<=javascript:gs.endOfToday()"); //^active=true
	personaGr.query();

	while(personaGr.next()) {
		users.push(personaGr.persona.toString());
		personaGr.active = false;
		personaGr.notes += "[Persona Manager][Daily: Maintain User Sessions - End] Ending persona session. Persona has been deleted, and groups/roles removed.";
		personaGr.update();
	}

	if(users.length > 0) {
		var userGr = new GlideRecord("sys_user");
		userGr.addEncodedQuery("sys_idIN" + users.join(","));
		userGr.active = false;
		userGr.locked_out = true;
		userGr.updateMultiple();

		var groupsGr = new GlideRecord("sys_user_grmember");
		groupsGr.addEncodedQuery("user.sys_idIN" + users.join(","));
		groupsGr.deleteMultiple();

		var rolesGr = new GlideRecord("sys_user_has_role");
		rolesGr.addEncodedQuery("user.sys_idIN" + users.join(","));
		rolesGr.deleteMultiple();
	}
} catch(e) {
	gs.error("[Persona Manager][Daily: Maintain User Sessions - End][ERR] " + e.message, "Persona Manager");
}
gs.info("[Persona Manager][Daily: Maintain User Sessions - End] Complete.", "Persona Manager");
